pub contract MaioriaGanha {
    pub var propostas: [String]
    pub let votos: {Int: Int}

    pub resource Voto {
        pub let propostas: [String]
        pub var escolha: {Int: Bool}

        init() {
            self.propostas = MaioriaGanha.propostas
            self.escolha = {}
            var i = 0
